   ae_int_t n2;
   ae_int_t info;
   ae_int_t info2;
   double y1;
   double y2;
   bool allsame;
   double ea;
   double eb;
   double varatested;
   double varbtested;
   double a;
   double b;
   double vara;
   double varb;
   double a2;
   double b2;
   double covab;
   double corrab;
   double p;
   ae_int_t qcnt;
   double f;
   double fp;
   double fm;
   double v;
   double vv;
   double cvrmserror;
   double cvavgerror;
   double cvavgrelerror;
   double rmserror;
   double avgerror;
   double avgrelerror;
   bool nondefect;
   double sinshift;
   double tasklevel;
   double noiselevel;
   double hstep;
   double sigma;
   double mean;
   double means;
   double stddev;
   double stddevs;
   bool slcerrors;
   bool slerrors;
   bool grcoverrors;
   bool gropterrors;
   bool gresterrors;
   bool grothererrors;
   bool grconverrors;
   bool waserrors;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewMatrix(xy2, 0, 0, DT_REAL, _state);
   NewVector(s, 0, DT_REAL, _state);
   NewVector(s2, 0, DT_REAL, _state);
   NewVector(w2, 0, DT_REAL, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(ta, 0, DT_REAL, _state);
   NewVector(tb, 0, DT_REAL, _state);
   NewVector(tc, 0, DT_REAL, _state);
   NewVector(xy0, 0, DT_REAL, _state);
   NewVector(tmpweights, 0, DT_REAL, _state);
   NewObj(linearmodel, w, _state);
   NewObj(linearmodel, wt, _state);
   NewObj(linearmodel, wt2, _state);
   NewVector(x1, 0, DT_REAL, _state);
   NewVector(x2, 0, DT_REAL, _state);
   NewVector(qtbl, 0, DT_REAL, _state);
   NewVector(qvals, 0, DT_REAL, _state);
   NewVector(qsigma, 0, DT_REAL, _state);
   NewObj(lrreport, ar, _state);
   NewObj(lrreport, ar2, _state);

// Primary settings
   maxn = 40;
   passcount = 3;
   estpasscount = 1000;
   sigmathreshold = 7.0;
   threshold = 1000000 * ae_machineepsilon;
   slerrors = false;
   slcerrors = false;
   grcoverrors = false;
   gropterrors = false;
   gresterrors = false;
   grothererrors = false;
   grconverrors = false;
