// Quantiles table setup
   qcnt = 5;
   ae_vector_set_length(&qtbl, qcnt - 1 + 1, _state);
   ae_vector_set_length(&qvals, qcnt - 1 + 1, _state);
   ae_vector_set_length(&qsigma, qcnt - 1 + 1, _state);
