// This function tests SMA(k) filter. It returns True on error.
//
// Additional IsSilent parameter controls detailed error reporting.
static bool testsma(bool issilent, ae_state *_state) {
   ae_frame _frame_block;
   bool precomputederrors;
   bool zerohandlingerrors;
   double threshold;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(x, 0, DT_REAL, _state);

   threshold = 1000 * ae_machineepsilon;
   if (!issilent) {
      printf("SMA(K) TEST\n");
   }
// Test several pre-computed problems.
//
// NOTE: tests below rely on the fact that floating point
//       additions and subtractions are exact when dealing
//       with integer values.
   precomputederrors = false;
   ae_vector_set_length(&x, 1, _state);
   x.xR[0] = 7.0;
   filtersma(&x, 1, 1, _state);
   precomputederrors = precomputederrors || x.xR[0] != 7.0;
   ae_vector_set_length(&x, 3, _state);
   x.xR[0] = 7.0;
   x.xR[1] = 8.0;
   x.xR[2] = 9.0;
   filtersma(&x, 3, 1, _state);
   precomputederrors = ((precomputederrors || x.xR[0] != 7.0) || x.xR[1] != 8.0) || x.xR[2] != 9.0;
   filtersma(&x, 3, 2, _state);
   precomputederrors = ((precomputederrors || x.xR[0] != 7.0) || x.xR[1] != 7.5) || x.xR[2] != 8.5;
   ae_vector_set_length(&x, 3, _state);
   x.xR[0] = 7.0;
   x.xR[1] = 8.0;
   x.xR[2] = 9.0;
   filtersma(&x, 3, 4, _state);
   precomputederrors = ((precomputederrors || x.xR[0] != 7.0) || x.xR[1] != 7.5) || x.xR[2] != 8.0;

// Test zero-handling:
// a) when we have non-zero sequence (N1 elements) followed by zero sequence
//    (N2 elements), then first N1+K-1 elements of the processed sequence are
//    non-zero, but elements since (N1+K)th must be exactly zero.
// b) similar property holds for zero sequence followed by non-zero one
//
// Naive implementation of SMA does not have such property.
//
// NOTE: it is important to initialize X with non-integer elements with long
// binary mantissas, because this test tries to test behaviour in the presence
// of roundoff errors, and it will be useless when used with integer inputs.
   zerohandlingerrors = false;
   ae_vector_set_length(&x, 10, _state);
   x.xR[0] = ae_sqrt(2.0, _state);
   x.xR[1] = ae_sqrt(3.0, _state);
   x.xR[2] = ae_sqrt(5.0, _state);
   x.xR[3] = ae_sqrt(6.0, _state);
   x.xR[4] = ae_sqrt(7.0, _state);
   x.xR[5] = 0.0;
   x.xR[6] = 0.0;
   x.xR[7] = 0.0;
   x.xR[8] = 0.0;
   x.xR[9] = 0.0;
   filtersma(&x, 10, 3, _state);
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[0] - ae_sqrt(2.0, _state), _state) > threshold;
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[1] - (ae_sqrt(2.0, _state) + ae_sqrt(3.0, _state)) / 2, _state) > threshold;
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[2] - (ae_sqrt(2.0, _state) + ae_sqrt(3.0, _state) + ae_sqrt(5.0, _state)) / 3, _state) > threshold;
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[3] - (ae_sqrt(3.0, _state) + ae_sqrt(5.0, _state) + ae_sqrt(6.0, _state)) / 3, _state) > threshold;
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[4] - (ae_sqrt(5.0, _state) + ae_sqrt(6.0, _state) + ae_sqrt(7.0, _state)) / 3, _state) > threshold;
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[5] - (ae_sqrt(6.0, _state) + ae_sqrt(7.0, _state)) / 3, _state) > threshold;
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[6] - ae_sqrt(7.0, _state) / 3, _state) > threshold;
   zerohandlingerrors = zerohandlingerrors || x.xR[7] != 0.0;
   zerohandlingerrors = zerohandlingerrors || x.xR[8] != 0.0;
   zerohandlingerrors = zerohandlingerrors || x.xR[9] != 0.0;
   x.xR[0] = 0.0;
   x.xR[1] = 0.0;
   x.xR[2] = 0.0;
   x.xR[3] = 0.0;
   x.xR[4] = 0.0;
   x.xR[5] = ae_sqrt(2.0, _state);
   x.xR[6] = ae_sqrt(3.0, _state);
   x.xR[7] = ae_sqrt(5.0, _state);
   x.xR[8] = ae_sqrt(6.0, _state);
   x.xR[9] = ae_sqrt(7.0, _state);
   filtersma(&x, 10, 3, _state);
   zerohandlingerrors = zerohandlingerrors || x.xR[0] != 0.0;
   zerohandlingerrors = zerohandlingerrors || x.xR[1] != 0.0;
   zerohandlingerrors = zerohandlingerrors || x.xR[2] != 0.0;
   zerohandlingerrors = zerohandlingerrors || x.xR[3] != 0.0;
   zerohandlingerrors = zerohandlingerrors || x.xR[4] != 0.0;
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[5] - ae_sqrt(2.0, _state) / 3, _state) > threshold;
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[6] - (ae_sqrt(2.0, _state) + ae_sqrt(3.0, _state)) / 3, _state) > threshold;
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[7] - (ae_sqrt(2.0, _state) + ae_sqrt(3.0, _state) + ae_sqrt(5.0, _state)) / 3, _state) > threshold;
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[8] - (ae_sqrt(3.0, _state) + ae_sqrt(5.0, _state) + ae_sqrt(6.0, _state)) / 3, _state) > threshold;
   zerohandlingerrors = zerohandlingerrors || ae_fabs(x.xR[9] - (ae_sqrt(5.0, _state) + ae_sqrt(6.0, _state) + ae_sqrt(7.0, _state)) / 3, _state) > threshold;

// Final result
   result = precomputederrors || zerohandlingerrors;
   ae_frame_leave(_state);
   return result;
}

