// === lda testing unit ===
static void testldaunit_gensimpleset(ae_int_t nfeatures, ae_int_t nclasses, ae_int_t nsamples, ae_int_t axis, RMatrix *xy, ae_state *_state);
static void testldaunit_gendeg1set(ae_int_t nfeatures, ae_int_t nclasses, ae_int_t nsamples, ae_int_t axis, RMatrix *xy, ae_state *_state);
static double testldaunit_generatenormal(double mean, double sigma, ae_state *_state);
static bool testldaunit_testwn(RMatrix *xy, RMatrix *wn, ae_int_t ns, ae_int_t nf, ae_int_t nc, ae_int_t ndeg, ae_state *_state);
static double testldaunit_calcj(ae_int_t nf, RMatrix *st, RMatrix *sw, RVector *w, double *p, double *q, ae_state *_state);
static void testldaunit_fishers(RMatrix *xy, ae_int_t npoints, ae_int_t nfeatures, ae_int_t nclasses, RMatrix *st, RMatrix *sw, ae_state *_state);

bool testlda(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t maxnf;
   ae_int_t maxns;
   ae_int_t maxnc;
   ae_int_t passcount;
   bool ldanerrors;
   bool lda1errors;
   bool waserrors;
   ae_int_t nf;
   ae_int_t nc;
   ae_int_t ns;
   ae_int_t i;
   ae_int_t info;
   ae_int_t pass;
   ae_int_t axis;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewMatrix(wn, 0, 0, DT_REAL, _state);
   NewVector(w1, 0, DT_REAL, _state);

// Primary settings
   maxnf = 10;
   maxns = 1000;
   maxnc = 5;
   passcount = 1;
   waserrors = false;
   ldanerrors = false;
   lda1errors = false;

// General tests
   for (nf = 1; nf <= maxnf; nf++) {
      for (nc = 2; nc <= maxnc; nc++) {
         for (pass = 1; pass <= passcount; pass++) {

         // Simple test for LDA-N/LDA-1
            axis = ae_randominteger(nf, _state);
            ns = maxns / 2 + ae_randominteger(maxns / 2, _state);
            testldaunit_gensimpleset(nf, nc, ns, axis, &xy, _state);
            fisherldan(&xy, ns, nf, nc, &info, &wn, _state);
            if (info != 1) {
               ae_set_error_flag(&ldanerrors, true, __FILE__, __LINE__, "testldaunit.ap:53");
               continue;
            }
            ae_set_error_flag(&ldanerrors, !testldaunit_testwn(&xy, &wn, ns, nf, nc, 0, _state), __FILE__, __LINE__, "testldaunit.ap:56");
            ae_set_error_flag(&ldanerrors, ae_fabs(wn.xyR[axis][0], _state) <= 0.75, __FILE__, __LINE__, "testldaunit.ap:57");
            fisherlda(&xy, ns, nf, nc, &info, &w1, _state);
            for (i = 0; i < nf; i++) {
               ae_set_error_flag(&lda1errors, w1.xR[i] != wn.xyR[i][0], __FILE__, __LINE__, "testldaunit.ap:60");
            }

         // Degenerate test for LDA-N
            if (nf >= 3) {
               ns = maxns / 2 + ae_randominteger(maxns / 2, _state);

            // there are two duplicate features,
            // axis is oriented along non-duplicate feature
               axis = ae_randominteger(nf - 2, _state);
               testldaunit_gendeg1set(nf, nc, ns, axis, &xy, _state);
               fisherldan(&xy, ns, nf, nc, &info, &wn, _state);
               if (info != 2) {
                  ae_set_error_flag(&ldanerrors, true, __FILE__, __LINE__, "testldaunit.ap:78");
                  continue;
               }
               ae_set_error_flag(&ldanerrors, ae_fabs(wn.xyR[axis][0], _state) <= 0.75, __FILE__, __LINE__, "testldaunit.ap:81");
               fisherlda(&xy, ns, nf, nc, &info, &w1, _state);
               for (i = 0; i < nf; i++) {
                  ae_set_error_flag(&lda1errors, w1.xR[i] != wn.xyR[i][0], __FILE__, __LINE__, "testldaunit.ap:84");
               }
            }
         }
      }
   }

// Final report
   waserrors = ldanerrors || lda1errors;
   if (!silent) {
      printf("LDA TEST\n");
      printf("FISHER LDA-N:                            ");
      if (!ldanerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("FISHER LDA-1:                            ");
      if (!lda1errors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      if (waserrors) {
         printf("TEST SUMMARY: FAILED\n");
      } else {
         printf("TEST SUMMARY: PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

// Generates 'simple' set - a sequence of unit 'balls' at (0,0), (1,0), (2,0)
// and so on.
static void testldaunit_gensimpleset(ae_int_t nfeatures, ae_int_t nclasses, ae_int_t nsamples, ae_int_t axis, RMatrix *xy, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   ae_int_t c;

   SetMatrix(xy);

   ae_assert(axis >= 0 && axis < nfeatures, "GenSimpleSet: wrong Axis!", _state);
   ae_matrix_set_length(xy, nsamples - 1 + 1, nfeatures + 1, _state);
   for (i = 0; i < nsamples; i++) {
      for (j = 0; j < nfeatures; j++) {
         xy->xyR[i][j] = testldaunit_generatenormal(0.0, 1.0, _state);
      }
      c = i % nclasses;
      xy->xyR[i][axis] = xy->xyR[i][axis] + c;
      xy->xyR[i][nfeatures] = (double)c;
   }
}

// Generates 'degenerate' set #1.
// NFeatures >= 3.
static void testldaunit_gendeg1set(ae_int_t nfeatures, ae_int_t nclasses, ae_int_t nsamples, ae_int_t axis, RMatrix *xy, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   ae_int_t c;

   SetMatrix(xy);

   ae_assert(axis >= 0 && axis < nfeatures, "GenDeg1Set: wrong Axis!", _state);
   ae_assert(nfeatures >= 3, "GenDeg1Set: wrong NFeatures!", _state);
   ae_matrix_set_length(xy, nsamples - 1 + 1, nfeatures + 1, _state);
   if (axis >= nfeatures - 2) {
      axis = nfeatures - 3;
   }
   for (i = 0; i < nsamples; i++) {
      for (j = 0; j < nfeatures - 1; j++) {
         xy->xyR[i][j] = testldaunit_generatenormal(0.0, 1.0, _state);
      }
      xy->xyR[i][nfeatures - 1] = xy->xyR[i][nfeatures - 2];
      c = i % nclasses;
      xy->xyR[i][axis] = xy->xyR[i][axis] + c;
      xy->xyR[i][nfeatures] = (double)c;
   }
}

// Normal random number
static double testldaunit_generatenormal(double mean, double sigma, ae_state *_state) {
   double u;
   double v;
   double sum;
   double result;

   result = mean;
   while (true) {
      u = (2 * ae_randominteger(2, _state) - 1) * ae_randomreal(_state);
      v = (2 * ae_randominteger(2, _state) - 1) * ae_randomreal(_state);
      sum = u * u + v * v;
      if (sum < 1.0 && sum > 0.0) {
         sum = ae_sqrt(-2 * ae_log(sum, _state) / sum, _state);
         result = sigma * u * sum + mean;
         break;
      }
   }
   return result;
}

// Tests WN for correctness
static bool testldaunit_testwn(RMatrix *xy, RMatrix *wn, ae_int_t ns, ae_int_t nf, ae_int_t nc, ae_int_t ndeg, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   double v;
   double wprev;
   double tol;
   double p;
   double q;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(st, 0, 0, DT_REAL, _state);
   NewMatrix(sw, 0, 0, DT_REAL, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewMatrix(z, 0, 0, DT_REAL, _state);
   NewVector(tx, 0, DT_REAL, _state);
   NewVector(jp, 0, DT_REAL, _state);
   NewVector(jq, 0, DT_REAL, _state);
   NewVector(work, 0, DT_REAL, _state);

   tol = 10000.0;
   result = true;
   testldaunit_fishers(xy, ns, nf, nc, &st, &sw, _state);

// Test for decreasing of J
   ae_vector_set_length(&tx, nf - 1 + 1, _state);
   ae_vector_set_length(&jp, nf - 1 + 1, _state);
   ae_vector_set_length(&jq, nf - 1 + 1, _state);
   for (j = 0; j < nf; j++) {
      ae_v_move(tx.xR, 1, &wn->xyR[0][j], wn->stride, nf);
      v = testldaunit_calcj(nf, &st, &sw, &tx, &p, &q, _state);
      jp.xR[j] = p;
      jq.xR[j] = q;
   }
   for (i = 1; i < nf - ndeg; i++) {
      result = result && jp.xR[i - 1] / jq.xR[i - 1] >= (1 - tol * ae_machineepsilon) * jp.xR[i] / jq.xR[i];
   }
   for (i = nf - 1 - ndeg + 1; i < nf; i++) {
      result = result && jp.xR[i] <= tol * ae_machineepsilon * jp.xR[0];
   }

// Test for J optimality
   ae_v_move(tx.xR, 1, wn->xyR[0], wn->stride, nf);
   v = testldaunit_calcj(nf, &st, &sw, &tx, &p, &q, _state);
   for (i = 0; i < nf; i++) {
      wprev = tx.xR[i];
      tx.xR[i] = wprev + 0.01;
      result = result && v >= (1 - tol * ae_machineepsilon) * testldaunit_calcj(nf, &st, &sw, &tx, &p, &q, _state);
      tx.xR[i] = wprev - 0.01;
      result = result && v >= (1 - tol * ae_machineepsilon) * testldaunit_calcj(nf, &st, &sw, &tx, &p, &q, _state);
      tx.xR[i] = wprev;
   }

// Test for linear independence of W
   ae_vector_set_length(&work, nf + 1, _state);
   ae_matrix_set_length(&a, nf - 1 + 1, nf - 1 + 1, _state);
   matrixmatrixmultiply(wn, 0, nf - 1, 0, nf - 1, false, wn, 0, nf - 1, 0, nf - 1, true, 1.0, &a, 0, nf - 1, 0, nf - 1, 0.0, &work, _state);
   if (smatrixevd(&a, nf, 1, true, &tx, &z, _state)) {
      result = result && tx.xR[0] > tx.xR[nf - 1] * 1000 * ae_machineepsilon;
   }
// Test for other properties
   for (j = 0; j < nf; j++) {
      v = ae_v_dotproduct(&wn->xyR[0][j], wn->stride, &wn->xyR[0][j], wn->stride, nf);
      v = ae_sqrt(v, _state);
      result = result && ae_fabs(v - 1, _state) <= 1000 * ae_machineepsilon;
      v = 0.0;
      for (i = 0; i < nf; i++) {
         v = v + wn->xyR[i][j];
      }
      result = result && v >= 0.0;
   }
   ae_frame_leave(_state);
   return result;
}

// Calculates J
static double testldaunit_calcj(ae_int_t nf, RMatrix *st, RMatrix *sw, RVector *w, double *p, double *q, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   double v;
   double result;

   ae_frame_make(_state, &_frame_block);
   *p = 0;
   *q = 0;
   NewVector(tx, 0, DT_REAL, _state);

   ae_vector_set_length(&tx, nf - 1 + 1, _state);
   for (i = 0; i < nf; i++) {
      v = ae_v_dotproduct(st->xyR[i], 1, w->xR, 1, nf);
      tx.xR[i] = v;
   }
   v = ae_v_dotproduct(w->xR, 1, tx.xR, 1, nf);
   *p = v;
   for (i = 0; i < nf; i++) {
      v = ae_v_dotproduct(sw->xyR[i], 1, w->xR, 1, nf);
      tx.xR[i] = v;
   }
   v = ae_v_dotproduct(w->xR, 1, tx.xR, 1, nf);
   *q = v;
   result = *p / (*q);
   ae_frame_leave(_state);
   return result;
}

// Calculates ST/SW
static void testldaunit_fishers(RMatrix *xy, ae_int_t npoints, ae_int_t nfeatures, ae_int_t nclasses, RMatrix *st, RMatrix *sw, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double v;

   ae_frame_make(_state, &_frame_block);
   SetMatrix(st);
   SetMatrix(sw);
   NewVector(c, 0, DT_INT, _state);
   NewVector(mu, 0, DT_REAL, _state);
   NewMatrix(muc, 0, 0, DT_REAL, _state);
   NewVector(nc, 0, DT_INT, _state);
   NewVector(tf, 0, DT_REAL, _state);
   NewVector(work, 0, DT_REAL, _state);

// Prepare temporaries
   ae_vector_set_length(&tf, nfeatures - 1 + 1, _state);
   ae_vector_set_length(&work, nfeatures + 1, _state);

// Convert class labels from reals to integers (just for convenience)
   ae_vector_set_length(&c, npoints - 1 + 1, _state);
   for (i = 0; i < npoints; i++) {
      c.xZ[i] = ae_round(xy->xyR[i][nfeatures], _state);
   }

// Calculate class sizes and means
   ae_vector_set_length(&mu, nfeatures - 1 + 1, _state);
   ae_matrix_set_length(&muc, nclasses - 1 + 1, nfeatures - 1 + 1, _state);
   ae_vector_set_length(&nc, nclasses - 1 + 1, _state);
   for (j = 0; j < nfeatures; j++) {
      mu.xR[j] = 0.0;
   }
   for (i = 0; i < nclasses; i++) {
      nc.xZ[i] = 0;
      for (j = 0; j < nfeatures; j++) {
         muc.xyR[i][j] = 0.0;
      }
   }
   for (i = 0; i < npoints; i++) {
      ae_v_add(mu.xR, 1, xy->xyR[i], 1, nfeatures);
      ae_v_add(muc.xyR[c.xZ[i]], 1, xy->xyR[i], 1, nfeatures);
      nc.xZ[c.xZ[i]] = nc.xZ[c.xZ[i]] + 1;
   }
   for (i = 0; i < nclasses; i++) {
      v = 1.0 / nc.xZ[i];
      ae_v_muld(muc.xyR[i], 1, nfeatures, v);
   }
   v = 1.0 / npoints;
   ae_v_muld(mu.xR, 1, nfeatures, v);

// Create ST matrix
   ae_matrix_set_length(st, nfeatures - 1 + 1, nfeatures - 1 + 1, _state);
   for (i = 0; i < nfeatures; i++) {
      for (j = 0; j < nfeatures; j++) {
         st->xyR[i][j] = 0.0;
      }
   }
   for (k = 0; k < npoints; k++) {
      ae_v_move(tf.xR, 1, xy->xyR[k], 1, nfeatures);
      ae_v_sub(tf.xR, 1, mu.xR, 1, nfeatures);
      for (i = 0; i < nfeatures; i++) {
         v = tf.xR[i];
         ae_v_addd(st->xyR[i], 1, tf.xR, 1, nfeatures, v);
      }
   }

// Create SW matrix
   ae_matrix_set_length(sw, nfeatures - 1 + 1, nfeatures - 1 + 1, _state);
   for (i = 0; i < nfeatures; i++) {
      for (j = 0; j < nfeatures; j++) {
         sw->xyR[i][j] = 0.0;
      }
   }
   for (k = 0; k < npoints; k++) {
      ae_v_move(tf.xR, 1, xy->xyR[k], 1, nfeatures);
      ae_v_sub(tf.xR, 1, muc.xyR[c.xZ[k]], 1, nfeatures);
      for (i = 0; i < nfeatures; i++) {
         v = tf.xR[i];
         ae_v_addd(sw->xyR[i], 1, tf.xR, 1, nfeatures, v);
      }
   }
   ae_frame_leave(_state);
}

