      ae_set_error_flag(err, ae_fabs(knnavgrelerror(&model1, &xy, npoints, _state) - refavgrel, _state) > 1.0E-6, __FILE__, __LINE__, "testknnunit.ap:401");
      testknnunit_testseterrors(&model1, nvars, nout, iscls, &testxy, testnpoints, &refavgce, &refcls0, &refcls1, &refrms, &refavg, &refavgrel, _state);
