   result = false;

// This test checks ability to train ensemble on simple regression
// problem "f(x0,x1,x2,...) = x0 + x1 + x2 + ...".
   eps = 5.0E-2;
   mnval = -1.0;
   mxval = 1.0;
   ntrain = 40;
