static void rec4serializationserialize(ae_serializer *s, rec4serialization *v, ae_state *_state) {
   ae_int_t i;

// boolean fields
   ae_serializer_serialize_int(s, v->b.cnt, _state);
   for (i = 0; i < v->b.cnt; i++) {
      ae_serializer_serialize_bool(s, v->b.xB[i], _state);
   }

// integer fields
   ae_serializer_serialize_int(s, v->i.cnt, _state);
   for (i = 0; i < v->i.cnt; i++) {
      ae_serializer_serialize_int(s, v->i.xZ[i], _state);
   }

// real fields
   ae_serializer_serialize_int(s, v->r.cnt, _state);
   for (i = 0; i < v->r.cnt; i++) {
      ae_serializer_serialize_double(s, v->r.xR[i], _state);
   }
}

static void rec4serializationunserialize(ae_serializer *s, rec4serialization *v, ae_state *_state) {
   ae_int_t i;
   ae_int_t k;
   bool bv;
   ae_int_t iv;
   double rv;

   SetObj(rec4serialization, v);

// boolean fields
   ae_serializer_unserialize_int(s, &k, _state);
   if (k > 0) {
      ae_vector_set_length(&v->b, k, _state);
      for (i = 0; i < k; i++) {
         ae_serializer_unserialize_bool(s, &bv, _state);
         v->b.xB[i] = bv;
      }
   }
// integer fields
   ae_serializer_unserialize_int(s, &k, _state);
   if (k > 0) {
      ae_vector_set_length(&v->i, k, _state);
      for (i = 0; i < k; i++) {
         ae_serializer_unserialize_int(s, &iv, _state);
         v->i.xZ[i] = iv;
      }
   }
// real fields
   ae_serializer_unserialize_int(s, &k, _state);
   if (k > 0) {
      ae_vector_set_length(&v->r, k, _state);
      for (i = 0; i < k; i++) {
         ae_serializer_unserialize_double(s, &rv, _state);
         v->r.xR[i] = rv;
      }
   }
}

