// Test R2 swaps
   ae_matrix_set_length(&r21, 1, 2, _state);
   ae_matrix_set_length(&r22, 2, 1, _state);
   r21.xyR[0][0] = 1.0;
   r21.xyR[0][1] = 2.0;
