// TestPoolSum: summation with pool
//
// We perform summation of 500000 numbers (each of them is equal to 1) in the
// recurrent manner, by accumulation of result in the pool.
//
// This test checks pool ability to handle continuous stream of operations.
//
// Returns True on success, False on failure.
static bool testalglibbasicsunit_performtestpoolsum(ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t sum;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(ae_shared_pool, pool, _state);
   RefObj(poolsummand, ptr, _state);
   NewObj(poolsummand, seed, _state);

   n = 500000;
   seed.val = 0;
   ae_shared_pool_set_seed(&pool, &seed, sizeof(seed), poolsummand_init, poolsummand_copy, poolsummand_free, _state);
   testalglibbasicsunit_parallelpoolsum(&pool, 0, n, _state);
   sum = 0;
   ae_shared_pool_first_recycled(&pool, &_ptr, _state);
   while (ptr != NULL) {
      sum = sum + ptr->val;
      ae_shared_pool_next_recycled(&pool, &_ptr, _state);
   }
   result = sum == n;
   ae_frame_leave(_state);
   return result;
}

// Summation routune for parallel summation test.
static void testalglibbasicsunit_parallelpoolsum(ae_shared_pool *sumpool, ae_int_t ind0, ae_int_t ind1, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;

   ae_frame_make(_state, &_frame_block);
   RefObj(poolsummand, ptr, _state);

   if (ind1 - ind0 <= 2) {
      ae_shared_pool_retrieve(sumpool, &_ptr, _state);
      ptr->val = ptr->val + ind1 - ind0;
      ae_shared_pool_recycle(sumpool, &_ptr, _state);
   } else {
      i = (ind0 + ind1) / 2;
      testalglibbasicsunit_parallelpoolsum(sumpool, ind0, i, _state);
      testalglibbasicsunit_parallelpoolsum(sumpool, i, ind1, _state);
   }
   ae_frame_leave(_state);
}

// This function merges sorted A[Idx0,Idx1) and A[Idx1,Idx2) into sorted array
// A[Idx0,Idx2) using corresponding elements of Buf.
static void testalglibbasicsunit_mergesortedarrays(ZVector *a, ZVector *buf, ae_int_t idx0, ae_int_t idx1, ae_int_t idx2, ae_state *_state) {
   ae_int_t srcleft;
   ae_int_t srcright;
   ae_int_t dst;

   srcleft = idx0;
   srcright = idx1;
   dst = idx0;
   while (true) {
      if (srcleft == idx1 && srcright == idx2) {
         break;
      }
      if (srcleft == idx1) {
         buf->xZ[dst] = a->xZ[srcright];
         srcright = srcright + 1;
         dst = dst + 1;
         continue;
      }
      if (srcright == idx2) {
         buf->xZ[dst] = a->xZ[srcleft];
         srcleft = srcleft + 1;
         dst = dst + 1;
         continue;
      }
      if (a->xZ[srcleft] < a->xZ[srcright]) {
         buf->xZ[dst] = a->xZ[srcleft];
         srcleft = srcleft + 1;
         dst = dst + 1;
      } else {
         buf->xZ[dst] = a->xZ[srcright];
         srcright = srcright + 1;
         dst = dst + 1;
      }
   }
   for (dst = idx0; dst < idx2; dst++) {
      a->xZ[dst] = buf->xZ[dst];
   }
}

